    ?l <http://schema.org/lat> ?lat .
    ?l <http://schema.org/lng> ?lng .
}
`;
query.execute(conn, 'lyon_bikes', "select ?lat ?lng where { ?l <http://schema.org/number> ?number . ?l <http://schema.org/lat> ?lat . ?l <http://schema.org/lng> ?lng .}", 'application/sparql-results+json', {
  limit: 10,
  offset: 0,
}).then(({ body }) => {
  const stores = []
  body.results.bindings.map(function(line){stores.push(Localisation(parseFloat(line.lat.value), parseFloat(line.lng.value)))}); 
  return(stores);
});
class BikeMap extends Component{
    constructor(props) {
        super(props);

        this.state = {
          stores: [
                  {latitude: 47.359423, longitude: -122.021071},
                  {latitude: 47.2052192687988, longitude: -121.988426208496},
                  {latitude: 47.6307081, longitude: -122.1434325},
                  {latitude: 47.3084488, longitude: -122.2140121},
                  {latitude: 47.5524695, longitude: -122.0425407}]
        }
      }

      displayMarkers = () => {
        return this.state.stores.map((store, index) => {
          return <Marker key={index} id={index} position={{
           lat: store.latitude,
           lng: store.longitude
         }}
         onClick={() => console.log("You clicked me!")} />
        })
      }
    render() {
        const mapStyles = {
            width: '100%',
            height: '100%',
          };
        return (
            <Map
              google={this.props.google}
              zoom={8}
              style={mapStyles}
              initialCenter={{ lat: 47.444, lng: -122.176}}
            >
              {this.displayMarkers()}
            </Map>
        );
      }
}



export default GoogleApiWrapper({
    apiKey: 'AIzaSyBE0oM95iQmrpxaKt7nREeHZwLqSe85nw0'
  })(BikeMap);
