import { Map, GoogleApiWrapper, Marker} from 'google-maps-react';
import React, { Component } from 'react';
import {query, Connection} from 'stardog';
import {
    dbName,
  } from "../../helpers/constants";
  const readQuery = `select ?lat ?lng 
where {
    ?l <http://schema.org/number> ?number .
    ?l <http://schema.org/lat> ?lat .
    ?l <http://schema.org/lng> ?lng .
}
`;
function Localisation(lat, lng) {
    return {
      latitude: lat,
      longitude: lng
    };
  }

  const conni = new Connection({
    username: "admin",
    password: "admin",
    endpoint: "http://localhost:5820"
  });


class BikeMap extends Component{
    constructor(props) {
        super(props);


        this.state = {
          stores: this.displayLocalisation()
        }
      }


      displayLocalisation() {
        query.execute(conni, dbName, readQuery).then(res => {
          if (!res.ok) {
            return;
          }

          const { top } = res.results.bindings;
          const store = []
          top.map(function(line){store.push(Localisation(parseFloat(line.lat.value), parseFloat(line.lng.value)))}); 
          return(top)
        });
      }

      displayMarkers = () => {
        return this.state.stores.map((store, index) => {
          return <Marker key={index} id={index} position={{
           lat: store.latitude,
           lng: store.longitude
         }}
         onClick={() => console.log("You clicked me!")} />
        })
      }
    render() {



        const mapStyles = {
            width: '100%',
            height: '100%',
          };
        return (
            <div>

            <Map
          google={this.props.google}
          zoom={8}
          style={mapStyles}
          initialCenter={{ lat: 45, lng: 4}}
        >
          {this.displayMarkers()}
        </Map>
        </div>
        );
      }
}



export default GoogleApiWrapper({
    apiKey: 'AIzaSyBE0oM95iQmrpxaKt7nREeHZwLqSe85nw0'
  })(BikeMap);
